# Intended for sourcing by other shell scripts
# This file is automatically generated by fs-package

. "$BUILD_ENV_DIR/scripts/env.sh"
. "$BUILD_ENV_DIR/../scripts/system.sh"

export PREFIX="$FSENV_PREFIX"
export REINSTALL="$FSENV_REINSTALL"
export INSTALLED="$PREFIX/installed/$PACKAGE-$REVISION"

mkdir -p $PREFIX/installed

if test -e $INSTALLED ; then
if [ "$REINSTALL" != "1" ]; then
echo "$INSTALLED"
exit 0
fi
fi

echo
echo $PACKAGE

rm -Rf "$BUILD_ENV_DIR/_temp" && mkdir "$BUILD_ENV_DIR/_temp" && cd "$BUILD_ENV_DIR/_temp"
