# Intended for sourcing by other shell scripts
# This file is automatically generated by fs-package

. "$BUILD_ENV_DIR/scripts/env.sh"
. "$BUILD_ENV_DIR/scripts/system.sh"

export PACKAGE_PREFIX="$BUILD_ENV_PREFIX"
# Deprecated:
export PREFIX="$BUILD_ENV_PREFIX"

export REINSTALL="$FSENV_REINSTALL"

if [ "$PACKAGE_NAME" != "" ]; then
export INSTALLED="$PREFIX/installed/$PACKAGE_NAME-$PACKAGE_VERSION-$PACKAGE_REVISION"
else
export INSTALLED="$PREFIX/installed/$PACKAGE-$PACKAGE_REVISION"
fi

mkdir -p $PREFIX/installed

if test -e $INSTALLED ; then
if [ "$REINSTALL" != "1" ]; then
echo "$INSTALLED"
exit 0
fi
fi

echo
echo $PACKAGE

rm -Rf "$BUILD_ENV_PREFIX/_build"
mkdir "$BUILD_ENV_PREFIX/_build"
cd "$BUILD_ENV_PREFIX/_build"

if [ "$PACKAGE_URL" != "" ]; then
if [ "$PACKAGE_CHECKSUM" != "" ]; then
python3 "$BUILD_ENV_DIR/scripts/download.py" "$PACKAGE_URL" "$PACKAGE_CHECKSUM"
fi
fi
